                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                       ,
          1                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            ,
          '"오늘은 지금까지의 글들을 다시 되돌아보는 시간을 가져봅시다\n\n\n  <블로그 생활 시작(?)>\n  \n  2012년 8/13일 월요일\n  \n   \n  예전 블로그 제목: 세상을 향한 외침\n  \n   \n  원래는 단순히 법무부 기사 제출만을 하려고 만든 블로그였지만,\n  \n   \n  개인적인, 평소에는 아무에게도 말하지 않았던 지극히 개인적인 이야기를 그냥 터놓고 써보고 싶다는 생각이 갑자기 들어서,\n  \n   \n  그리고 개인적으로 나에게 큰 관심을 가지고는 있지만 나에대해 잘 모르고있는 사람들에게\n  \n   \n  내가 누군지 정말 깊이 알려주고 싶어서\n  \n   \n  이제 조금씩 써보려 합니다\n  \n   \n  이게 얼마나 오래갈지는 잘 모르겠지만, 시간 날때마다 조금씩 기록하다보면 쌓여서\n  \n   \n  나중에 시간이 지나서도 추억이 되지 않을까 하는 마음도 있고\n  \n   \n  많은 사람들이랑 대화도 나눠보고 싶고\n  \n   \n  \n  작은 기대를 가지고 시작해봅니다.\n  \n   \n  다들 잘 부탁합니다.\n  \n   \n  -훈훈한 시작\n  \n   \n  <가볍게 첫 시작은 자기소개>\n  \n  소수에게 법무부 블로그 기자단 명함을 통해 번호와 주소를 알려줬으니 몇분은 찾아 오겠지만 (소수의 기준은 당연히 제게 의미있는 분들이지요ㅎㅎ)\n  \n   \n  -은근히 당신이 그 소수중 한사람이라는 것을 알려주는 대목\n  \n   \n   \n  <나의 성격?>\n  \n  성격을 바로 단정지어 말하지 않고, 왜 지금의 성격을 가지게 되었는지\n  \n  과거부터 파헤치면서 깊게 알려드릴께요.\n  \n   \n  -뭔가 모르는 사람들에게 자기를 알린다는 그 부담감이 알게 모르게 있어서\n  \n  좀 경직됬다라 해야하나\n  \n  첫날에 긴장을 많이 했지요.\n  \n   \n  내가 이걸 올리면 사람들 반응이 어떨까?\n  \n   \n  반응요?\n  \n  네. 없어요 ㅋ (알잖아요)\n  \n  심지어 아직까지도...\n  \n   \n  <어제 쓴거 보니까>\n  \n  어제 쓴 내용들을 다시 보니까 너무 솔직하게 써버린것 같아서 왠지...\n  \n   \n  아... 그냥 내 속이 다 드러나버린것만 같아서\n  \n   \n  좀 부끄럽긴 하지만, 엄밀히 말하자면 진짜 저게 제 진심일지도 몰라요 ㅎ\n  \n   \n  뭔가 막 가려서 덮어서 아무도 모르게 하는 것 보다\n  \n   \n  이 사람 이런때도 있었구나 하는 진심을 들려주는 정말로 저를 공감하게 하는\n  \n   \n  재미있는 글이지 않을까 하는 생각이 들어서\n  \n   \n  내버려 둡니다\n  \n   \n  -처음부터 대담하게 드러내고 시작 하는게\n  \n  쉽진 않잖아요?\n  \n  솔직한 블로그...\n  \n   \n   \n  <나의 이상형?>\n  \n   \n  태그를 제 이름으로 해놔서\n  \n  그걸 통해 오시는 분이 있는 듯 해요 ㅎ\n  \n  물론 이렇게 되라고 한거지만\n  \n   \n  실제로 만나서 저를 아시는 분일텐데\n  \n  제게 관심이 있어서 온다면\n  \n  과연 누구시려나?\n  \n   \n  검색을 해서 올 정도의 정성이라면\n  \n  관심이 엄청난 거일텐데\n  \n   \n  잠깐 생각한 후 내린 결론은\n  \n   \n  님 혹시 저 좋아하세요?\n  \n   \n  뭐 이러이러 해서\n  \n  이번엔 제 이상형을 생각해보는 시간을\n  \n  가져봅니다 ㅎㅎ\n  \n   \n   \n  -그 한사람은 당연히 당신이라 예측했었어요.\n  \n  맞지요?\n  \n   \n  <저를 사랑하고 있는 당신을 위해>\n  \n  오히려 주변 사람들 중에선\n  \n  제가 호감을 갖는 분이 있지요\n  \n   \n  친하냐고요?\n  \n  말했지요?\n  \n  저와 여자와의 관계는\n  \n  관심이 없거나\n  \n  그쪽이 접근을 못한답니다.\n  \n  소통의 단절\n  \n   \n  그래서 제가 좀 편해보인다거나\n  \n  좀 없어보이면\n  \n  덜 외롭게될까라는 생각도 들어요.\n  \n  부담안가지고 편하게 대하면 되는데\n  \n  내가 뭐\n  \n  만날때마다 인상짓고\n  \n  화내는 것도 아니고\n  \n   \n  친절하게 대해주는데...\n  \n   \n  오히려 그것때문에\n  \n  접근을 못해;;\n  \n  설레는 건가\n  \n  눈도 제대로 못맞추고\n  \n   \n  -ㅇㅇ 당신이에요.\n  \n   \n  <외로움>\n  \n  엄밀히 말하자면\n  \n  제가 외롭다는 것을 안게\n  \n  블로그를 시작하기 불과 몇일전이에요.\n  \n   \n  그동안 외롭지만\n  \n  애써 자신의 진짜모습을 외면해왔다랄까\n  \n   \n  그걸 인정해버리면\n  \n  무척 힘들걸 생각해버리고\n  \n  무의식적으로 부정한것 같아요\n  \n   \n  점점 혼자서 지내는 시간은 많아지고\n  \n  생각은 많아지는데\n  \n  그걸 공감해줄 사람이 없다는게\n  \n  그게 너무 슬픈거에요.\n  \n   \n  다들 각자의 삶에 빠져\n  \n  다른사람 신경쓸 겨를도 없지요.\n  \n   \n  그렇지만\n  \n  지금까지 살아오면서 속깊은 대화를\n  \n  들어줄 사람이\n  \n  없다는게\n  \n  딱히 있어도 막 말하고 싶은게\n  \n  속에서 넘치는건 아니지만\n  \n  그냥\n  \n  그럴 사람이 없다는것\n  \n  정말로 제 옆에 있는 사람이 없다는것이\n  \n  슬픈거에요.\n  \n   \n   \n  가족중에서는 대화상대가 없고\n  \n  또래애들은 저처럼 깊이 생각하는 애가\n  \n  없으므로\n  \n  (제가 너무 빨맀다랄까 )\n  \n   \n  고립됬어요.\n  \n   \n  그래서\n  \n  그래서\n  \n  누구든 상관없으니\n  \n  내 이야기를 들어줘\n  \n  라는 불특정다수에게\n  \n  이런 지극히 개인적인 이야기를\n  \n  하는 것이지요.\n  \n   \n   \n  누구도\n  \n  저의 이런 모습을\n  \n  진짜 나를 아는 분은\n  \n  없어요.\n  \n   \n  그저 착하고 생각이 많고 행실이 바른\n  \n  모범생 정도로만 알지...\n  \n   \n   \n  이런 절 아는분은\n  \n  이 글을 읽는 여러분이\n  \n  유일해요 ㅎ\n  \n   \n   \n   \n  이러이러하여\n  \n  제게 첫사랑의 기준은\n  \n   \n  이런 제 진짜모습을\n  \n  알아주고\n  \n  이해하고\n  \n  감싸주는\n  \n   \n  그런사람이에요.\n  \n   \n   \n  지금까지와의 사람들과는 다른\n  \n  저를 알아준 유일한 사람\n  \n   \n  저는 그 사람을 절대로\n  \n  어떤 이유로든\n  \n  평생\n  \n  미워할 수 없을거에요.\n  \n  평생 제 사랑으로 남겠지요.\n  \n   \n  제게 사랑은 이런거에요.\n  \n  단순한 호기심이 아닌\n  \n  진실된 관계...\n  \n  서로가 서로에게 기댈 수 있으면서\n  \n  힘이 되어주고\n  \n  위로하는\n  \n  각자의 삶을 더 풍요롭게 하는\n  \n  그런... 진짜 사랑\n  \n   \n  나이에 안맞는 건가?\n  \n   \n   \n  개인적으론 이거 말고도\n  \n  다른 이유도 있습니다.\n  \n   \n  ... 지금 솔직히 저는\n  \n  딱 무언가 이뤄야 한다는\n  \n  간절한 무언가가\n  \n  없어요.\n  \n   \n  왜 사는지\n  \n  어떻게 살아야 하는지를\n  \n  답하면\n  \n  나만의 답이 나오면\n  \n  그저 행복하게 성취해가면서\n  \n  늘 활발히 지낼 줄 알았는데,\n  \n  삶의 목표가 뚜렷해져서\n  \n  정신없이 몰두하면서 살줄 알았는데,\n  \n   \n  어떻게 살지는 정했지만\n  \n   \n  삶의 이유\n  \n  내가 꼭 살아야가야만 하는 이유\n  \n   \n  아직 제겐 없네요\n  \n   \n  답이 사랑일까요?\n  \n   \n  나를 위해\n  \n  그리고 날 믿어준 사랑을 위해\n  \n  열심히 살아갈까?\n  \n   \n  나 스스로를 버리게 만들정도로\n  \n  미치도록 사랑에 빠지면\n  \n  그 사랑을 위해서라도\n  \n  나는 변화할까?\n  \n   \n  모르겠습니다\n  \n   \n  그저 학생으로서는 절대\n  \n  행해선 안된다고 여긴\n  \n  금지된 행위에 대한\n  \n  막연한 갈망일지도 모르지요.\n  \n   \n  그렇지만\n  \n  지금\n  \n   \n  누군가가 제 삶을 끌어주고\n  \n  제가 살아가는 이유가 됬으면 하는\n  \n  소망이 있습니다\n  \n   \n  그렇네요...\n  \n   \n   \n  이러는 저를 저도 잘 모르겠네요\n  \n  갑자기\n  \n  간절해졌어요\n  \n   \n  어쩌면 지금 제가 누군가를\n  \n  마음속 깊이 좋아하는지도 모르겠습니다.\n  \n  저도 모르게...\n  \n   \n   \n  -정말 깊이 우러나와서 쓴 글이었죠.\n  \n   \n  좋아하는 사람?\n  \n  ...말 안할래요\n  \n   \n   \n  <진지하게 생각해봤는데>\n  \n   \n  한사람만 저에게 호감을 느끼면\n  \n  아무 문제 없고 저도 좋지만\n  \n   \n  많은 분들이 저에게 호감을 느끼고\n  \n  저랑 사귀는 상상을 하며\n  \n  행복해하다가\n  \n   \n  어느순간 제가\n  \n  님들\n  \n  저 애인 생겼어요\n  \n  해버리면\n  \n   \n  ...\n  \n   \n  ...\n  \n   \n   \n  의도치 않게 저 나쁜사람 되잖아요?\n  \n   \n   \n  제가 이상형이니 사랑이니\n  \n  이런 글을 좀 나중에 쓸걸하고\n  \n  지금 살짝... 후회중\n  \n   \n   \n  -걱정... 괜히 사람들에게 상처 주는거 아닐까?\n  \n   \n  그렇지만 의외로 꿋꿋이 참아내시네요?\n  \n  걱정 안해도 될걸 그랬어요\n  \n   \n   \n  <사랑... 일단 기다려요>\n  \n   \n  만나서 사귀어 달라는 것\n  \n  절대 아닙니다.\n  \n   \n  한마디 해주세요.\n  \n   \n  정신좀 차리게\n  \n   \n   \n  지금 널 진지하게 좋아하고 있다고\n  \n   \n  앞으로 계속 만나고 싶다고\n  \n   \n  너와 함께 있고싶다고\n  \n   \n  사랑한다고\n  \n   \n   \n   \n  무리한 부탁입니다.\n  \n  알아요.\n  \n  이게 얼마나\n  \n  힘든지\n  \n  엄두조차 못낼 일인지\n  \n   \n  그렇지만\n  \n  이런건 하루라도 빠르면 빠를수록\n  \n  제 미래는\n  \n  크게 바뀔꺼에요.\n  \n   \n  당신 스스로\n  \n  저를 진심으로 오래 만나고 싶어한다면\n  \n  당신을 위하는 것도 있겠지만\n  \n  저를 위해\n  \n  제 앞날을 위해\n  \n   \n  제 삶의 이유가 되어주세요.\n  \n   \n   \n  그렇지만 너무 제게 빠져있지는 마셔요\n  \n   \n  미래가 어떻게 될지는 아무도 모르는 거니\n  \n   \n  그렇지만 만일 붙잡는다면\n  \n  사라지지 않을께요.\n  \n  평생 함께할께요.\n  \n  \n  당신의 이야기를 들어줄\n  \n  좋은사람으로밖에\n  \n  남아줄 수 밖에 없을지 몰라도\n  \n  당신이 겪는 힘든일들을\n  \n  더이상 힘들어 하지 않게\n  \n  제가 늘 함께해드릴께요.\n  \n   \n   \n  뭐, 그런데 이런 분이 있으려나...\n  \n   \n  저를 그토록 사랑해줄 사람이\n  \n   \n   \n  없겠지요??\n  \n   \n  -없는 것 같아요\n  \n   \n  아무리 부탁해도 안하는걸