                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                ,
          '"개인적인, 평소에는 아무에게도 말하지 않았던 지극히 개인적인 이야기를 그냥 터놓고 써보고 싶다는 생각이 갑자기 들어서,\n\n\n  그리고 개인적으로 나에게 큰 관심을 가지고는 있지만 나에대해 잘 모르고있는 사람들에게\n  \n   \n  내가 누군지 정말 깊이 알려주고 싶어서\n  \n   \n  \n   \n  이제 조금씩 써보려 합니다\n  \n   \n  ...\n  \n   \n   \n   \n  태그를 제 이름으로 해놔서\n  \n  그걸 통해 오시는 분이 있는 듯 해요 ㅎ\n  \n  물론 이렇게 되라고 한거지만\n  \n   \n  실제로 만나서 저를 아시는 분일텐데\n  \n  제게 관심이 있어서 온다면\n  \n  과연 누구시려나?\n  \n   \n  검색을 해서 올 정도의 정성이라면\n  \n  관심이 엄청난 거일텐데\n  \n   \n  잠깐 생각한 후 내린 결론은\n  \n   \n  혹시 저 좋아하세요?\n  \n   \n  뭐 이러이러 해서\n  \n  이번엔 제 이상형을 생각해보는 시간을\n  \n  가져봅니다 ㅎㅎ\n  \n   \n  물론 이렇게 쓴다고 이상형을 만나는 건\n  \n  아니겠지만...\n  \n   \n   \n   \n   \n   \n  ...\n  \n   \n   \n  결론은\n  \n  당신이 바라만 보면서\n  \n  제가 다가오기를\n  \n  기다리기만 하면\n  \n   \n  전 제가 늘 해왔듯이\n  \n  그냥\n  \n  지나갈겁니다.\n  \n   \n  사랑이라고 착각하지 마세요\n  \n   \n  그러나 당신이 진심이다.\n  \n   \n  정말 이게 사랑이다고 느끼면\n  \n   \n  다가오세요.\n  \n   \n   \n   \n  ...\n  \n   \n   \n   \n  엄밀히 말하자면\n  \n  제가 외롭다는 것을 안게\n  \n  블로그를 시작하기 불과 몇일전이에요.\n  \n   \n  그동안 외롭지만\n  \n  애써 자신의 진짜모습을 외면해왔다랄까\n  \n   \n  그걸 인정해버리면\n  \n  무척 힘들것이라 생각해버리고\n  \n  무의식적으로 부정한 것 같아요\n  \n   \n  점점 혼자서 지내는 시간은 많아지고\n  \n  생각은 많아지는데\n  \n  그걸 공감해줄 사람이 없다는 게\n  \n  그게 너무 슬픈거에요.\n  \n   \n  다들 각자의 삶에 빠져\n  \n  다른사람 신경쓸 겨를도 없지요.\n  \n   \n  그렇지만\n  \n  지금까지 살아오면서 속깊은 대화를\n  \n  들어줄 사람이\n  \n  없다는게\n  \n  딱히 있어도 막 말하고 싶은게\n  \n  속에서 넘치는건 아니지만\n  \n  그냥\n  \n  그럴 사람이 없다는것\n  \n  정말로 제 옆에 있는 사람이 없다는 것\n  \n   \n   \n  ...\n  \n   \n   \n  삶의 이유\n  \n  내가 꼭 살아야가야만 하는 이유\n  \n   \n  아직 제겐 없네요\n  \n   \n  답이 사랑일까요?\n  \n   \n  나를 위해\n  \n  그리고 날 믿어준 사랑을 위해\n  \n  열심히 살아갈까?\n  \n   \n  나 스스로를 버리게 만들정도로\n  \n  미치도록 사랑에 빠지면\n  \n  그 사랑을 위해서라도\n  \n  나는 변화할까?\n  \n   \n  모르겠습니다\n  \n   \n  그저 학생으로서는 절대\n  \n  행해선 안된다고 여긴\n  \n  금지된 행위에 대한\n  \n  막연한 갈망일지도 모르지요.\n  \n   \n  그렇지만\n  \n  지금\n  \n   \n  누군가가 제 삶을 끌어주고\n  \n  제가 살아가는 이유가 됬으면 하는\n  \n  소망이 있습니다\n  \n   \n  그렇네요...\n  \n   \n   \n  이러는 저를 저도 잘 모르겠네요\n  \n  갑자기\n  \n  간절해졌어요\n  \n   \n  어쩌면 지금 제가 누군가를\n  \n  마음속 깊이 좋아하는지도 모르겠습니다.\n  \n  저도 